import React, { useState, useEffect, useRef } from "react";
import "./GameBoard.css";

const GameBoard = ({ targetAngle, isTargetVisible, onGuess }) => {
  const [needleAngle, setNeedleAngle] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const boardRef = useRef(null);

  const handleMouseDown = (e) => {
    if (!isTargetVisible) {
      setIsDragging(true);
      updateNeedleAngle(e);
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging && !isTargetVisible) {
      updateNeedleAngle(e);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const updateNeedleAngle = (e) => {
    if (!boardRef.current) return;
    const rect = boardRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const angle = Math.atan2(e.clientY - centerY, e.clientX - centerX) * (180 / Math.PI);
    const normalizedAngle = angle > 90 ? angle - 360 : angle;
    setNeedleAngle(normalizedAngle);
    onGuess(normalizedAngle);
  };

  useEffect(() => {
    if (isDragging) {
      window.addEventListener("mousemove", handleMouseMove);
      window.addEventListener("mouseup", handleMouseUp);
      return () => {
        window.removeEventListener("mousemove", handleMouseMove);
        window.removeEventListener("mouseup", handleMouseUp);
      };
    }
  }, [isDragging]);

  return (
    <div className="board" ref={boardRef}>
      <div
        className="target-area"
        style={{
          background: isTargetVisible
            ? `conic-gradient(
                #ff4757 0deg 45deg,
                #ffa502 45deg 90deg,
                #1e90ff 90deg 135deg,
                #ffa502 135deg 180deg,
                #ff4757 180deg 225deg,
                #ffa502 225deg 270deg,
                #1e90ff 270deg 315deg,
                #ffa502 315deg 360deg
              )`
            : "#dfe4ea",
        }}
      >
        {isTargetVisible && (
          <>
            <div className="segment-number" style={{ top: "10%", left: "50%" }}>2</div>
            <div className="segment-number" style={{ top: "30%", right: "20%" }}>3</div>
            <div className="segment-number" style={{ top: "30%", left: "20%" }}>3</div>
            <div className="segment-number" style={{ top: "50%", right: "10%" }}>4</div>
            <div className="segment-number" style={{ top: "50%", left: "10%" }}>4</div>
            <div className="segment-number" style={{ top: "70%", right: "20%" }}>3</div>
            <div className="segment-number" style={{ top: "70%", left: "20%" }}>3</div>
            <div className="segment-number" style={{ top: "90%", left: "50%" }}>2</div>
          </>
        )}
      </div>
      <div
        className="needle-container"
        style={{ transform: `rotate(${needleAngle}deg)` }}
      >
        <div className="needle" />
      </div>
      {isTargetVisible && (
        <div
          className="target-line"
          style={{ transform: `rotate(${targetAngle}deg)` }}
        />
      )}
    </div>
  );
};

export default GameBoard;
