import React, { useState, useEffect } from "react";
import SpectrumArc from "./SpectrumArc";
import { spectrumPairs } from "../data/spectrumPairs";
import "./Game.css";

const Game = () => {
  const [currentPair, setCurrentPair] = useState(null);
  const [targetPosition, setTargetPosition] = useState(0);
  const [guessPosition, setGuessPosition] = useState(null);
  const [isTargetVisible, setIsTargetVisible] = useState(true);
  const [score, setScore] = useState({ player1: 0, player2: 0 });
  const [round, setRound] = useState(1);
  const [isPlayer1Turn, setIsPlayer1Turn] = useState(false);

  useEffect(() => {
    startNewRound();
  }, []);

  const startNewRound = () => {
    const randomPair =
      spectrumPairs[Math.floor(Math.random() * spectrumPairs.length)];
    const randomPosition = Math.random();
    setCurrentPair(randomPair);
    setTargetPosition(randomPosition);
    setGuessPosition(null);
    setIsTargetVisible(true);
    setIsPlayer1Turn(false);
  };

  const handleHideTarget = () => {
    setIsTargetVisible(false);
    setIsPlayer1Turn(true);
  };

  const handleGuess = (position) => {
    if (isPlayer1Turn && !isTargetVisible) {
      setGuessPosition(position);
    }
  };

  const calculateScore = () => {
    if (guessPosition === null) return 0;
    const difference = Math.abs(targetPosition - guessPosition);
    if (difference < 0.1) return 4;
    if (difference < 0.2) return 3;
    if (difference < 0.3) return 2;
    if (difference < 0.4) return 1;
    return 0;
  };

  const handleReveal = () => {
    const points = calculateScore();
    setScore((prev) => ({
      ...prev,
      player1: prev.player1 + points,
    }));
  };

  const startNewGame = () => {
    setScore({ player1: 0, player2: 0 });
    setRound(1);
    startNewRound();
  };

  return (
    <div className="game-container">
      <h1>Wavelength</h1>

      <div className="spectrum-labels">
        <span className="label left">{currentPair?.left}</span>
        <span className="label right">{currentPair?.right}</span>
      </div>

      <SpectrumArc
        targetPosition={targetPosition}
        guessPosition={guessPosition}
        isTargetVisible={isTargetVisible}
        onGuess={handleGuess}
      />

      <div className="controls">
        {isTargetVisible ? (
          <button className="hide-target" onClick={handleHideTarget}>
            Hide Target
          </button>
        ) : (
          <button className="reveal" onClick={handleReveal}>
            Reveal
          </button>
        )}
        <button className="next-round" onClick={startNewRound}>
          Next Round
        </button>
        <button className="new-game" onClick={startNewGame}>
          New Game
        </button>
      </div>

      <div className="score-board">
        <div className="score">
          <h3>Player 1 Score</h3>
          <p>{score.player1}</p>
        </div>
        <div className="score">
          <h3>Player 2 Score</h3>
          <p>{score.player2}</p>
        </div>
      </div>
    </div>
  );
};

export default Game;
