import React, { useState, useEffect, useRef } from "react";
import "./GameBoard.css";

const GameBoard = ({ targetAngle, isTargetVisible, onGuess }) => {
  const [needleAngle, setNeedleAngle] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const boardRef = useRef(null);

  const handleMouseDown = (e) => {
    if (!isTargetVisible) {
      setIsDragging(true);
      updateNeedleAngle(e);
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging && !isTargetVisible) {
      updateNeedleAngle(e);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const updateNeedleAngle = (e) => {
    if (!boardRef.current) return;
    const rect = boardRef.current.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.bottom;
    const angle = Math.atan2(e.clientX - centerX, centerY - e.clientY) * (180 / Math.PI);
    const clampedAngle = Math.max(-90, Math.min(90, angle));
    setNeedleAngle(clampedAngle);
    onGuess(clampedAngle);
  };

  useEffect(() => {
    if (isDragging) {
      window.addEventListener("mousemove", handleMouseMove);
      window.addEventListener("mouseup", handleMouseUp);
      return () => {
        window.removeEventListener("mousemove", handleMouseMove);
        window.removeEventListener("mouseup", handleMouseUp);
      };
    }
  }, [isDragging]);

  return (
    <div className="board" ref={boardRef}>
      <div
        className="target-area"
        style={{
          display: isTargetVisible ? "block" : "none",
          background: `conic-gradient(
            from -90deg at 50% 100%,
            #ff4757 0deg 45deg,
            #ffa502 45deg 90deg,
            #1e90ff 90deg 135deg,
            #ffa502 135deg 180deg,
            #ff4757 180deg 225deg,
            #ffa502 225deg 270deg,
            #1e90ff 270deg 315deg,
            #ffa502 315deg 360deg
          )`,
        }}
      >
        {isTargetVisible && (
          <>
            <div className="segment-number" style={{ top: "20%", left: "50%" }}>2</div>
            <div className="segment-number" style={{ top: "40%", right: "25%" }}>3</div>
            <div className="segment-number" style={{ top: "40%", left: "25%" }}>3</div>
            <div className="segment-number" style={{ top: "60%", right: "15%" }}>4</div>
            <div className="segment-number" style={{ top: "60%", left: "15%" }}>4</div>
            <div className="segment-number" style={{ top: "80%", right: "25%" }}>3</div>
            <div className="segment-number" style={{ top: "80%", left: "25%" }}>3</div>
            <div className="segment-number" style={{ top: "95%", left: "50%" }}>2</div>
          </>
        )}
      </div>
      <div
        className="needle-container"
        style={{ transform: `rotate(${needleAngle}deg)` }}
      >
        <div className="needle" />
      </div>
      {isTargetVisible && (
        <div
          className="target-line"
          style={{ transform: `rotate(${targetAngle}deg)` }}
        />
      )}
    </div>
  );
};

export default GameBoard;
