import React, { useState, useEffect, useRef } from "react";
import "./GameBoard.css";

const GameBoard = ({ targetAngle, isTargetVisible, onGuess, roundNumber }) => {
  const [needleAngle, setNeedleAngle] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const needleContainerRef = useRef(null);

  // Reset states when round changes
  useEffect(() => {
    setNeedleAngle(0);
  }, [roundNumber]);

  useEffect(() => {
    const handleStart = (e) => {
      if (!isTargetVisible) {
        setIsDragging(true);
        e.preventDefault();
      }
    };

    const handleMove = (e) => {
      if (!isDragging || isTargetVisible) return;
      e.preventDefault();

      const rect = needleContainerRef.current.getBoundingClientRect();
      const centerX = rect.left + rect.width / 2;
      const centerY = rect.bottom;

      let clientX, clientY;
      if (e.type === "touchmove") {
        clientX = e.touches[0].clientX;
        clientY = e.touches[0].clientY;
      } else {
        clientX = e.clientX;
        clientY = e.clientY;
      }

      const angle =
        (Math.atan2(clientX - centerX, centerY - clientY) * 180) / Math.PI;
      const clampedAngle = Math.max(-90, Math.min(90, angle));
      setNeedleAngle(clampedAngle);
      onGuess(clampedAngle);
    };

    const handleEnd = () => {
      setIsDragging(false);
    };

    const needleContainer = needleContainerRef.current;
    if (needleContainer) {
      needleContainer.addEventListener("mousedown", handleStart);
      document.addEventListener("mousemove", handleMove);
      document.addEventListener("mouseup", handleEnd);
      needleContainer.addEventListener("touchstart", handleStart);
      document.addEventListener("touchmove", handleMove);
      document.addEventListener("touchend", handleEnd);
    }

    return () => {
      if (needleContainer) {
        needleContainer.removeEventListener("mousedown", handleStart);
        document.removeEventListener("mousemove", handleMove);
        document.removeEventListener("mouseup", handleEnd);
        needleContainer.removeEventListener("touchstart", handleStart);
        document.removeEventListener("touchmove", handleMove);
        document.removeEventListener("touchend", handleEnd);
      }
    };
  }, [isDragging, isTargetVisible, onGuess]);

  return (
    <div className="board">
      <div
        className="target-area"
        style={{
          display: isTargetVisible ? "block" : "none",
          background: `conic-gradient(
            from -90deg at 50% 100%,
            #a4b0be 0deg ${targetAngle - 22.5 + 90}deg,
            #ff6b6b ${targetAngle - 22.5 + 90}deg ${targetAngle - 13.5 + 90}deg,
            #feca57 ${targetAngle - 13.5 + 90}deg ${targetAngle - 4.5 + 90}deg,
            #48dbfb ${targetAngle - 4.5 + 90}deg ${targetAngle + 4.5 + 90}deg,
            #feca57 ${targetAngle + 4.5 + 90}deg ${targetAngle + 13.5 + 90}deg,
            #ff6b6b ${targetAngle + 13.5 + 90}deg ${targetAngle + 22.5 + 90}deg,
            #a4b0be ${targetAngle + 22.5 + 90}deg 180deg
          )`,
        }}
      />
      <div className="needle-container" ref={needleContainerRef}>
        <div
          className="needle"
          style={{
            transform: `rotate(${needleAngle}deg)`,
            display: !isTargetVisible || (isTargetVisible && needleAngle !== 0) ? "block" : "none",
          }}
        />
      </div>
    </div>
  );
};

export default GameBoard;
