import React, { useState, useEffect, useRef } from "react";
import "./GameBoard.css";

const GameBoard = ({ targetAngle, isTargetVisible, onGuess }) => {
  const [needleAngle, setNeedleAngle] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const boardRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (isDragging && boardRef.current) {
        const rect = boardRef.current.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.bottom;
        const mouseX = e.clientX;
        const mouseY = e.clientY;

        let angle = Math.atan2(mouseX - centerX, centerY - mouseY) * (180 / Math.PI);
        angle = Math.max(-90, Math.min(90, angle));

        setNeedleAngle(angle);
      }
    };

    const handleMouseUp = () => {
      if (isDragging) {
        setIsDragging(false);
        onGuess(needleAngle);
      }
    };

    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
    document.addEventListener("touchmove", handleMouseMove);
    document.addEventListener("touchend", handleMouseUp);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
      document.removeEventListener("touchmove", handleMouseMove);
      document.removeEventListener("touchend", handleMouseUp);
    };
  }, [isDragging, needleAngle, onGuess]);

  const handleMouseDown = () => {
    setIsDragging(true);
  };

  const getBackgroundGradient = () => {
    if (!isTargetVisible) {
      return "conic-gradient(from 0deg at 50% 100%, #e0e0e0 0deg 180deg)";
    }

    const startAngle = targetAngle - 90;
    return `conic-gradient(
      from ${startAngle}deg at 50% 100%,
      #e0e0e0 0deg 45deg,
      #ff6b6b 45deg 63deg,
      #ffd93d 63deg 81deg,
      #6c5ce7 81deg 99deg,
      #ffd93d 99deg 117deg,
      #ff6b6b 117deg 135deg,
      #e0e0e0 135deg 180deg
    )`;
  };

  return (
    <div className="board" ref={boardRef}>
      <div
        className="target-area"
        style={{ background: getBackgroundGradient() }}
      >
        {isTargetVisible && (
          <div className="points-container">
            <div
              className="segment-points"
              style={{ transform: "rotate(-27deg)" }}
            >
              2
            </div>
            <div
              className="segment-points"
              style={{ transform: "rotate(-9deg)" }}
            >
              3
            </div>
            <div
              className="segment-points"
              style={{ transform: "rotate(9deg)" }}
            >
              4
            </div>
            <div
              className="segment-points"
              style={{ transform: "rotate(27deg)" }}
            >
              3
            </div>
            <div
              className="segment-points"
              style={{ transform: "rotate(45deg)" }}
            >
              2
            </div>
          </div>
        )}
      </div>
      <div
        className="needle-container"
        onMouseDown={handleMouseDown}
        onTouchStart={handleMouseDown}
      >
        <div
          className="needle"
          style={{ transform: `rotate(${needleAngle}deg)` }}
        />
      </div>
    </div>
  );
};

export default GameBoard;
